/**
 * Description: This method sends a GET request to the specified URL and returns true if the response code is 200 and the response body is not empty.
 * 
 * @param path The URL to send the GET request to.
 * @param headers A map of headers to include in the request.
 * @return true if the GET request is successful and the response body is not empty, false otherwise.
 * @throws IOException if an I/O error occurs when sending the request.
 * @see org.springframework.util.CollectionUtils#isEmpty(java.util.Collection)
 */
public static boolean get(String path, Map<String, String> headers) {
    try {
        URL url = new URL(path);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestMethod("GET");
        connection.setConnectTimeout(10000);
        connection.setReadTimeout(10000);
        connection.setDoOutput(true);
        connection.setDoInput(true);
        connection.setUseCaches(false);
        connection.setInstanceFollowRedirects(true);
        connection.setRequestProperty("Content-Type", "application/json");
        connection.setRequestProperty("Accept", "application/json");
        if (!CollectionUtils.isEmpty(headers)) {
            for (Map.Entry<String, String> entry : headers.entrySet()) {
                connection.setRequestProperty(entry.getKey(), entry.getValue());
            }
        }
        connection.connect();
        int responseCode = connection.getResponseCode();
        if (responseCode == 200) {
            BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                if (StringUtils.hasText(line)) {
                    return true;
                }
            }
        }
    } catch (IOException e) {
        logger.error("Error sending GET request to {}: {}", path, e.getMessage());
    }
    return false;
}
