public static String replaceTable(String sql, String tableName) {
    if (StringUtils.isEmpty(sql)) {
        return sql;
    }
    try {
        Select select = (Select) CCJSqlParserUtil.parse(sql);
        PlainSelect plainSelect = (PlainSelect) select.getSelectBody();
        Table table = plainSelect.getFromItem();
        if (table instanceof SubSelect) {
            SubSelect subSelect = (SubSelect) table;
            Table subTable = subSelect.getSelect().getFromItem();
            if (subTable instanceof Table) {
                Table newTable = new Table(tableName);
                subSelect.setFromItem(newTable);
            }
        } else if (table instanceof Table) {
            Table newTable = new Table(tableName);
            plainSelect.setFromItem(newTable);
        }
        return CCJSqlParserUtil.toSQLString(select);
    } catch (JSQLParserException e) {
        log.error("Error while parsing the SQL query: {}", sql, e);
        return sql;
    }
}
