public static String getExpandUrl(String url, Object paramObj) {
    if (paramObj instanceof Map) {
        Map<String, String> paramMap = (Map<String, String>) paramObj;
        return UriComponentsBuilder.fromHttpUrl(url)
                .queryParams(paramMap)
                .build()
                .toUriString();
    } else {
        BeanWrapper beanWrapper = new BeanWrapperImpl(paramObj);
        List<String> paramList = beanWrapper.getPropertyDescriptors()
                .stream()
                .map(PropertyDescriptor::getName)
                .filter(propertyName -> !propertyName.equals("class"))
                .map(propertyName -> {
                    try {
                        return (String) beanWrapper.getPropertyValue(propertyName);
                    } catch (Exception e) {
                        return null;
                    }
                })
                .filter(Objects::nonNull)
                .collect(Collectors.toList());
        return UriComponentsBuilder.fromHttpUrl(url)
                .queryParams(paramList)
                .build()
                .toUriString();
    }
}
