/**
 * Generates a map of key-value pairs where the key is a constant string and the value is a JSON example string.
 * The map is either empty or contains a single entry.
 *
 * @param modelName the name of the model to generate an example for.
 * @return an unmodifiable map containing the generated key-value pair, or an empty map if the model name is null or the schema is not found.
 */
public Map<String, String> generate(String modelName) {
    if (modelName == null) {
        return Collections.emptyMap();
    }

    Optional<Schema> schema = CatsGlobalContext.get().getSwagger().getComponents().getSchemas().get(modelName);
    if (!schema.isPresent()) {
        return Collections.emptyMap();
    }

    Schema schemaValue = schema.get();
    if (schemaValue instanceof ObjectSchema) {
        ObjectSchema objectSchema = (ObjectSchema) schemaValue;
        Map<String, String> result = new HashMap<>();
        result.put(modelName, JsonUtils.toJson(objectSchema.getExample()));
        return Collections.unmodifiableMap(result);
    } else if (schemaValue instanceof ArraySchema) {
        ArraySchema arraySchema = (ArraySchema) schemaValue;
        if (arraySchema.getItems() instanceof ObjectSchema) {
            ObjectSchema objectSchema = (ObjectSchema) arraySchema.getItems();
            Map<String, String> result = new HashMap<>();
            result.put(modelName, JsonUtils.toJson(objectSchema.getExample()));
            return Collections.unmodifiableMap(result);
        }
    }

    return Collections.emptyMap();
}
