public void put(String path, long position, ByteBuf data) {
    // Lock the writeLock
    writeLock.lock();
    // Compute the cache for the given path if it doesn't exist
    if (!cache.containsKey(path)) {
        cache.put(path, new HashMap<>());
    }
    // Find the floor entry of the position in the cache
    NavigableMap<Long, Value> floorEntry = cache.get(path).floorEntry(position);
    // If the floor entry doesn't exist or the position is after the end of the floor entry, create a new entry
    if (floorEntry == null || floorEntry.getKey() > position) {
        floorEntry = cache.get(path).put(position, new Value(new BitSet(), 0));
    }
    // Ensure the capacity of the cache and update the cache index if necessary
    ensureCapacity(path);
    // Create a new Value object with the updated blocks and data length
    Value value = new Value(floorEntry.getValue().blocks, floorEntry.getValue().dataLength + data.readableBytes());
    // Put the new entry into the cache and the LRU cache
    cache.get(path).put(position, value);
    lruCache.put(path, position);
    // Write the data to the cache
    writeData(path, position, data);
    // Unlock the writeLock
    writeLock.unlock();
}
