public ListenableFuture<Void> queue(ExecuteEntry executeEntry, Poller poller, Duration timeout) {
  ExecuteOperationMetadata metadata =
      ExecuteOperationMetadata.newBuilder()
          .setActionDigest(executeEntry.getActionDigest())
          .setStdoutStreamName(executeEntry.getStdoutStreamName())
          .setStderrStreamName(executeEntry.getStderrStreamName())
          .build();
  Operation operation =
      Operation.newBuilder()
          .setName(executeEntry.getOperationName())
          .setMetadata(Any.pack(metadata))
          .build();
  Digest actionDigest = executeEntry.getActionDigest();
  ActionKey actionKey = DigestUtil.asActionKey(actionDigest);

  Stopwatch stopwatch = Stopwatch.createStarted();
  ListenableFuture<Boolean> cachedResultFuture;
  if (executeEntry.getSkipCacheLookup()) {
    cachedResultFuture = immediateFuture(false);
  } else {
    cachedResultFuture =
        checkCacheFuture(actionKey, operation, executeEntry.getRequestMetadata());
  }
  return transformAsync(
      cachedResultFuture,
      (cachedResult) -> {
        if (cachedResult) {
          poller.pause();
          long checkCacheUSecs = stopwatch.elapsed(MICROSECONDS);
          log.log(
              Level.FINER,
              format(
                  "ServerInstance(%s): checkCache(%s): %sus elapsed",
                  getName(), operation.getName(), checkCacheUSecs));
          return IMMEDIATE_VOID_FUTURE;
        }
        return transformAndQueue(executeEntry, poller, operation, stopwatch, timeout);
      },
      operationTransformService);
}

public static ByteString retainKeyPrefix(ByteString tenantNS, List<String> topicFilterLevels) {
    ByteString prefix = ByteString.empty();
    byte leastLevels = 0;
    boolean singleLevelWildcard = false;
    for (int i = 0; i < topicFilterLevels.size(); i++) {
        String tfl = topicFilterLevels.get(i);
        if ("+".equals(tfl)) {
            leastLevels++;
            singleLevelWildcard = true;
            continue;
        }
        if ("#".equals(tfl)) {
            break;
        }
        leastLevels++;
        if (!singleLevelWildcard) {
            prefix = prefix.concat(copyFromUtf8(tfl));
        }
        if (i + 1 < topicFilterLevels.size()) {
            if (!topicFilterLevels.get(i + 1).equals("#") && !singleLevelWildcard) {
                prefix = prefix.concat(copyFromUtf8(NUL));
            }
        }
    }
    return tenantNS.concat(unsafeWrap(new byte[] {leastLevels})).concat(prefix);
}

