public static ByteString retainKeyPrefix(ByteString tenantNS, List<String> topicFilterLevels) {
    if (tenantNS == null || topicFilterLevels == null || topicFilterLevels.isEmpty()) {
        return null;
    }
    String tenantNSString = tenantNS.toStringUtf8();
    if (tenantNSString.isEmpty()) {
        return null;
    }
    String keyPrefix = tenantNSString;
    for (String topicFilterLevel : topicFilterLevels) {
        if (topicFilterLevel == null || topicFilterLevel.isEmpty()) {
            continue;
        }
        keyPrefix = keyPrefix + TopicUtil.NUL + TopicUtil.escape(topicFilterLevel);
    }
    return ByteString.copyFromUtf8(keyPrefix);
}
