        .subAggregation(AggregationBuilders.terms(ListViewTemplate.VERSION).field(
            ListViewTemplate.VERSION).order(SortOrder.DESC).subAggregation(
            AggregationBuilders.terms(ListViewTemplate.ID).field(ListViewTemplate.ID)
                .order(SortOrder.DESC)));
    searchSourceBuilder.aggregation(aggregationBuilder);

