/**
 * Clones the given object using different strategies depending on its type.
 * If the object is a String, it returns the object as is.
 * If the object is a Collection, it finds the first non-null element and clones the collection using the object mapper if the first element is not serializable.
 * If the object is a Map, it finds the first non-null entry and clones the map using the object mapper if the key or value is not serializable.
 * If the object is a JsonNode, it returns a deep copy of the node.
 * If the object is serializable, it clones the object using the serialization helper.
 * If the object is not serializable, it clones the object using a JSON marshaling strategy.
 *
 * @param object the object to clone
 * @param <T> the type of the object
 * @return the cloned object
 */
@Override
public<T> T clone(T object) {
    if (object instanceof String) {
        return (T) object;
    } else if (object instanceof Collection) {
        Collection<T> collection = (Collection<T>) object;
        if (collection.isEmpty()) {
            return (T) collection;
        }
        T first = collection.iterator().next();
        if (first == null) {
            return (T) collection;
        }
        if (first instanceof Serializable) {
            return (T) SerializationHelper.clone(first);
        }
        return (T) objectMapper.convertValue(collection, collection.getClass());
    } else if (object instanceof Map) {
        Map<T, T> map = (Map<T, T>) object;
        if (map.isEmpty()) {
            return (T) map;
        }
        T firstKey = map.keySet().iterator().next();
        T firstValue = map.get(firstKey);
        if (firstKey == null || firstValue == null) {
            return (T) map;
        }
        if (firstKey instanceof Serializable && firstValue instanceof Serializable) {
            return (T) SerializationHelper.clone(map);
        }
        return (T) objectMapper.convertValue(map, map.getClass());
    } else if (object instanceof JsonNode) {
        return (T) ((JsonNode) object).deepCopy();
    } else if (object instanceof Serializable) {
        return (T) SerializationHelper.clone(object);
    } else {
        return (T) objectMapper.convertValue(object, object.getClass());
    }
}
