public static void unTarZip(String tarFilePath, String targetDirectoryPath) throws CoreException {
    try {
        File targetDirectory = new File(targetDirectoryPath);
        if (!targetDirectory.exists()) {
            throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Target directory does not exist.");
        }
        if (!targetDirectory.isDirectory()) {
            throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Target directory is not a directory.");
        }
        if (targetDirectory.isFile()) {
            throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Target directory is a file.");
        }
        File tarFile = new File(tarFilePath);
        if (!tarFile.exists()) {
            throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Tar file does not exist.");
        }
        if (!tarFile.isFile()) {
            throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Tar file is not a file.");
        }
        TarArchiveInputStream tarInputStream = new TarArchiveInputStream(new GzipCompressorInputStream(new FileInputStream(tarFile)));
        TarArchiveEntry tarEntry;
        while ((tarEntry = tarInputStream.getNextTarEntry()) != null) {
            if (!tarEntry.isDirectory()) {
                File parentDirectory = new File(targetDirectory, FilenameUtils.getFullPath(tarEntry.getName()));
                if (!parentDirectory.exists()) {
                    if (!parentDirectory.mkdirs()) {
                        throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Failed to create parent directories.");
                    }
                }
                File outputFile = new File(targetDirectory, tarEntry.getName());
                if (!outputFile.exists()) {
                    if (!outputFile.createNewFile()) {
                        throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Failed to create output file.");
                    }
                }
                OutputStream outputStream = new FileOutputStream(outputFile);
                IOUtils.copy(tarInputStream, outputStream);
                outputStream.close();
            }
        }
        tarInputStream.close();
    } catch (IOException e) {
        throw new CoreException(PDKRunnerErrorCodes.ERROR_CODE_1000, "Failed to decompress tar.gz file.");
    }
}
