public static ByteBuffer decompress(byte[] metrics, CompressionType compressionType) {
    if (metrics == null) {
        throw new IllegalArgumentException("metrics cannot be null");
    }
    if (compressionType == null) {
        throw new IllegalArgumentException("compressionType cannot be null");
    }
    if (compressionType == CompressionType.NONE) {
        return ByteBuffer.wrap(metrics);
    }
    if (compressionType == CompressionType.GZIP) {
        try {
            return ByteBuffer.wrap(GzipUtils.decompress(metrics));
        } catch (IOException e) {
            throw new KafkaException(Errors.COMPRESSION_ERROR, "Failed to decompress metrics", e);
        }
    }
    if (compressionType == CompressionType.SNAPPY) {
        try {
            return ByteBuffer.wrap(SnappyUtils.decompress(metrics));
        } catch (IOException e) {
            throw new KafkaException(Errors.COMPRESSION_ERROR, "Failed to decompress metrics", e);
        }
    }
    throw new IllegalArgumentException("Unknown compression type: " + compressionType);
}
