        return ac.createAcls(toAdd)
            .doOnSuccess(v -> {
              log.info("{} new ACLs created", toAdd.size());
              log.info("Starting ACLs deletion");
            })
            .then(ac.deleteAcls(toDelete)
                .doOnSuccess(v -> log.info("{} ACLs deleted", toDelete.size())));
      }));
