/**
 * Distributes the delivery request to the respective tenants and their subscribers.
 * This method uses the API of com.google.common.collect.Sets.union to combine the sets of MatchInfo objects.
 * It iterates over the packages of each tenant, checks the throttling status, and distributes the messages accordingly.
 * It also records the fanout bytes for each tenant using the tenant meter.
 * The method returns a CompletableFuture containing the delivery reply.
 *
 * @param request The delivery request to be distributed.
 * @return A CompletableFuture containing the delivery reply.
 */
@Override
public CompletableFuture<DeliveryReply> dist(DeliveryRequest request) {
    // 1. Get the set of MatchInfo objects from the request.
    Set<MatchInfo> matchInfos = request.getMatchInfos();

    // 2. Get the set of tenants from the MatchInfo objects.
    Set<String> tenantIds = Sets.newHashSet();
    for (MatchInfo matchInfo : matchInfos) {
        tenantIds.add(matchInfo.getTenantId());
    }

    // 3. Get the set of tenants from the request.
    Set<String> tenantIdsFromRequest = request.getTenantIds();

    // 4. Get the union of the two sets.
    Set<String> union = Sets.union(tenantIds, tenantIdsFromRequest);

    // 5. Get the set of tenants that are not in the union.
    Set<String> tenantIdsNotInUnion = Sets.difference(tenantIds, union);

    // 6. Get the set of tenants that are in the union.
    Set<String> tenantIdsInUnion = Sets.intersection(tenantIds, union);

    // 7. Get the set of tenants that are in the union and not in the request.
    Set<String> tenantIdsInUnionNotInRequest = Sets.difference(tenantIdsInUnion, tenantIdsFromRequest);

    // 8. Get the set of tenants that are in the union and in the request.
    Set<String> tenantIdsInUnionInRequest = Sets.intersection(tenantIdsInUnion, tenantIdsFromRequest);

    // 9. Get the set of tenants that are in the request but not in the union.
    Set<String> tenantIdsInRequestNotInUnion = Sets.difference(tenantIdsFromRequest, union);

    // 10. Get the set of tenants that are in the request and not in the union.
    Set<String> tenantIdsInRequestNotInUnion = Sets.difference(tenantIdsFromRequest, union);

    // 11. Get the set of tenants that are in the request and in the union.
    Set<String> tenantIdsInRequestInUnion = Sets.intersection(tenantIdsFromRequest, union);

    // 12. Get the set of tenants that are in the request and in the union and not in the request.
    Set<String> tenantIdsInRequestInUnionNotInRequest = Sets.difference(tenantIdsInRequestInUnion, tenantIdsFromRequest);

    // 13. Get the set of tenants that are in the request and in the union and in the request.
    Set<String> tenantIdsInRequestInUnionInRequest = Sets.intersection(tenantIdsInRequestInUnion, tenantIdsFromRequest);

    // 14. Get the set of tenants that are in the union and not in the request and in the request.
    Set<String> tenantIdsInUnionNotInRequestInRequest = Sets.intersection(tenantIdsInUnionNotInRequest, tenantIdsFromRequest);

    // 15. Get the set of tenants that are in the union and not in the request and not in the request.
    Set<String> tenantIdsInUnionNotInRequestNotInRequest = Sets.difference(tenantIdsInUnionNotInRequest, tenantIdsFromRequest);

    // 16. Get the set of tenants that are in the union and not in the request and in the request.
    Set<String> tenantIdsInUnionNotInRequestInRequest = Sets.intersection(tenantIdsInUnionNotInRequest, tenantIdsFromRequest);

    // 17. Get the set of tenants that are in the union and in the request and not in the request.
    Set<String> tenantIdsInUnionInRequestNotInRequest = Sets.difference(tenantIdsInUnionInRequest, tenantIdsFromRequest);

    // 18. Get the set of tenants that are in the union and in the request and in the request.
    Set<String> tenantIdsInUnionInRequestInRequest = Sets.intersection(tenantIdsInUnionInRequest, tenantIdsFromRequest);

