public ComponentXO readComponent(final Repository repository, final EntityId componentId) {
    // 1. Check if the component exists.
    final Component component = repository.getComponent(componentId);
    if (component == null) {
        throw new WebApplicationException(Response.Status.NOT_FOUND);
    }

    // 2. Check if the user has the necessary permissions.
    final Set<String> permissions = new HashSet<>();
    permissions.add(BreadActions.READ);
    permissions.add(BreadActions.READ_ASSETS);
    final boolean hasPermissions = repository.getPermissionChecker().hasPermissions(repository, permissions);
    if (!hasPermissions) {
        throw new AuthorizationException("User does not have the necessary permissions.");
    }

    // 3. Convert the component into a ComponentXO object.
    final ComponentXO componentXO = new ComponentXO();
    componentXO.setId(componentId);
    componentXO.setName(component.getName());
    componentXO.setAssets(new ArrayList<>());
    for (final Asset asset : component.getAssets()) {
        final AssetXO assetXO = new AssetXO();
        assetXO.setId(asset.getId());
        assetXO.setPath(asset.getPath());
        assetXO.setMimeType(asset.getMimeType());
        assetXO.setLastModified(asset.getLastModified());
        componentXO.getAssets().add(assetXO);
    }

    return componentXO;
}
