@VisibleForTesting
boolean handleRecoveryTask(RecoveryTaskMetadata recoveryTaskMetadata) {
  LOG.info("Started handling the recovery task: {}", recoveryTaskMetadata);
  long startTime = System.nanoTime();
  Timer.Sample taskTimer = Timer.start(meterRegistry);

  PartitionOffsets partitionOffsets =
      validateKafkaOffsets(
          adminClient,
          recoveryTaskMetadata,
          AstraConfig.getRecoveryConfig().getKafkaConfig().getKafkaTopic());
  long offsetsValidatedTime = System.nanoTime();
  long consumerPreparedTime = 0, messagesConsumedTime = 0, rolloversCompletedTime = 0;

  if (partitionOffsets != null) {
    RecoveryTaskMetadata validatedRecoveryTask =
        new RecoveryTaskMetadata(
            recoveryTaskMetadata.name,
            recoveryTaskMetadata.partitionId,
            partitionOffsets.startOffset,
            partitionOffsets.endOffset,
            recoveryTaskMetadata.createdTimeEpochMs);

    if (partitionOffsets.startOffset != recoveryTaskMetadata.startOffset
        || recoveryTaskMetadata.endOffset != partitionOffsets.endOffset) {
      recoveryRecordsNoLongerAvailable.increment(
          (partitionOffsets.startOffset - recoveryTaskMetadata.startOffset)
              + (partitionOffsets.endOffset - recoveryTaskMetadata.endOffset));
    }

    try {
      RecoveryChunkManager<LogMessage> chunkManager =
          RecoveryChunkManager.fromConfig(
              meterRegistry,
              searchMetadataStore,
              snapshotMetadataStore,
              AstraConfig.getIndexerConfig(),
              blobFs,
              AstraConfig.getS3Config());

      // Ingest data in parallel
      LogMessageWriterImpl logMessageWriterImpl = new LogMessageWriterImpl(chunkManager);
      AstraKafkaConsumer kafkaConsumer =
          new AstraKafkaConsumer(
              makeKafkaConfig(
                  AstraConfig.getRecoveryConfig().getKafkaConfig(),
                  validatedRecoveryTask.partitionId),
              logMessageWriterImpl,
              meterRegistry);

      kafkaConsumer.prepConsumerForConsumption(validatedRecoveryTask.startOffset);
      consumerPreparedTime = System.nanoTime();
      kafkaConsumer.consumeMessagesBetweenOffsetsInParallel(
          AstraKafkaConsumer.KAFKA_POLL_TIMEOUT_MS,
          validatedRecoveryTask.startOffset,
          validatedRecoveryTask.endOffset);
      messagesConsumedTime = System.nanoTime();
      // Wait for chunks to upload.
      boolean success = chunkManager.waitForRollOvers();
      rolloversCompletedTime = System.nanoTime();
      // Close the recovery chunk manager and kafka consumer.
      kafkaConsumer.close();
      chunkManager.stopAsync();
      chunkManager.awaitTerminated(DEFAULT_START_STOP_DURATION);
      LOG.info("Finished handling the recovery task: {}", validatedRecoveryTask);
      taskTimer.stop(recoveryTaskTimerSuccess);
      return success;
    } catch (Exception ex) {
      LOG.error("Exception in recovery task [{}]: {}", validatedRecoveryTask, ex);
      taskTimer.stop(recoveryTaskTimerFailure);
      return false;
    } finally {
      long endTime = System.nanoTime();
      LOG.info(
          "Recovery task {} took {}ms, (subtask times offset validation {}, consumer prep {}, msg consumption {}, rollover {})",
          recoveryTaskMetadata,
          nanosToMillis(endTime - startTime),
          nanosToMillis(offsetsValidatedTime - startTime),
          nanosToMillis(consumerPreparedTime - offsetsValidatedTime),
          nanosToMillis(messagesConsumedTime - consumerPreparedTime),
          nanosToMillis(rolloversCompletedTime - messagesConsumedTime));
    }
  } else {
    LOG.info(
        "Recovery task {} data no longer available in Kafka (validation time {}ms)",
        recoveryTaskMetadata,
        nanosToMillis(offsetsValidatedTime - startTime));
    recoveryRecordsNoLongerAvailable.increment(
        recoveryTaskMetadata.endOffset - recoveryTaskMetadata.startOffset + 1);
    return true;
  }
}

protected void updateRollingFileAppender(LogConfiguration logConfiguration) {
    org.apache.logging.log4j.core.config.Configuration config = context.getConfiguration();
    Appender appender = context.getRootLogger().getAppenders().get("rollingFileAppender");
    RollingFileAppender rollingFileAppender = null;
    if (appender instanceof RollingFileAppender) {
        rollingFileAppender = (RollingFileAppender) appender;
    }
    if(null == rollingFileAppender){
        return;
    }
    RollingFileManager manager = rollingFileAppender.getManager();
    CompositeTriggeringPolicy compositeTriggeringPolicy = LogUtil.getCompositeTriggeringPolicy(logConfiguration.getLogSaveSize().toString());
    String glob = "tapdata-agent-*.log.*.gz";
    DeleteAction deleteAction = LogUtil.getDeleteAction(logConfiguration.getLogSaveTime(), Application.logsPath, glob, config);
    Action[] actions = {deleteAction};
    DefaultRolloverStrategy strategy = DefaultRolloverStrategy.newBuilder()
            .withMax(logConfiguration.getLogSaveCount().toString())
            .withCustomActions(actions)
            .withConfig(config)
            .build();
    manager.setRolloverStrategy(strategy);
    manager.setTriggeringPolicy(compositeTriggeringPolicy);
}

void shutdown() {
  LOG.info("Running shutdown hook.");
  try {
    serviceManager.stopAsync().awaitStopped(30, TimeUnit.SECONDS);
  } catch (Exception e) {
    // stopping timed out
    LOG.error("ServiceManager shutdown timed out", e);
  }
  try {
    curatorFramework.unwrap().close();
  } catch (Exception e) {
    LOG.error("Error while closing curatorFramework ", e);
  }
  LOG.info("Shutting down LogManager");
  LogManager.shutdown();
}

public long determineStartingOffset(
    long currentEndOffsetForPartition,
    long currentBeginningOffsetForPartition,
    AstraConfigs.IndexerConfig indexerConfig) {
  // Filter stale snapshots for partition.
  if (partitionId == null) {
    LOG.warn("PartitionId can't be null.");
  }

  List<SnapshotMetadata> snapshots = snapshotMetadataStore.listSync();
  List<SnapshotMetadata> snapshotsForPartition =
      snapshots.stream()
          .filter(
              snapshotMetadata -> {
                if (snapshotMetadata == null || snapshotMetadata.partitionId == null) {
                  LOG.warn(
                      "snapshot metadata or partition id can't be null: {} ",
                      Strings.join(snapshots, ','));
                }
                return snapshotMetadata != null
                    && snapshotMetadata.partitionId != null
                    && snapshotMetadata.partitionId.equals(partitionId);
              })
          .collect(Collectors.toUnmodifiableList());
  List<SnapshotMetadata> deletedSnapshots = deleteStaleLiveSnapshots(snapshotsForPartition);

  List<SnapshotMetadata> nonLiveSnapshotsForPartition =
      snapshotsForPartition.stream()
          .filter(s -> !deletedSnapshots.contains(s))
          .collect(Collectors.toUnmodifiableList());

  // Get the highest offset that is indexed in durable store.
  List<RecoveryTaskMetadata> recoveryTasks = recoveryTaskMetadataStore.listSync();
  long highestDurableOffsetForPartition =
      getHighestDurableOffsetForPartition(
          nonLiveSnapshotsForPartition, recoveryTasks, partitionId);
  LOG.debug(
      "The highest durable offset for partition {} is {}",
      partitionId,
      highestDurableOffsetForPartition);

  if (highestDurableOffsetForPartition <= 0) {
    LOG.info("There is no prior offset for this partition {}.", partitionId);

    // If the user wants to start at the current offset in Kafka and _does not_ want to create
    // recovery tasks to backfill, then we can just return the current offset.
    // If the user wants to start at the current offset in Kafka and _does_ want to create
    // recovery tasks to backfill, then we create the recovery tasks needed and then return
    // the current offset for the indexer. And if the user does _not_ want to start at the
    // current offset in Kafka, then we'll just default to the old behavior of starting from
    // the very beginning
    if (!indexerConfig.getCreateRecoveryTasksOnStart()
        && indexerConfig.getReadFromLocationOnStart()
            == AstraConfigs.KafkaOffsetLocation.LATEST) {
      LOG.info(
          "CreateRecoveryTasksOnStart is set to false and ReadLocationOnStart is set to current. Reading from current and"
              + " NOT spinning up recovery tasks");
      return currentEndOffsetForPartition;
    } else if (indexerConfig.getCreateRecoveryTasksOnStart()
        && indexerConfig.getReadFromLocationOnStart()
            == AstraConfigs.KafkaOffsetLocation.LATEST) {
      // Todo - this appears to be able to create recovery tasks that have a start and end
      // position of 0, which is invalid. This seems to occur when new clusters are initialized,
      // and is  especially problematic when indexers are created but never get assigned (ie,
      // deploy 5, only assign 3).
      LOG.info(
          "CreateRecoveryTasksOnStart is set and ReadLocationOnStart is set to current. Reading from current and"
              + " spinning up recovery tasks");
      createRecoveryTasks(
          partitionId,
          currentBeginningOffsetForPartition,
          currentEndOffsetForPartition,
          indexerConfig.getMaxMessagesPerChunk());
      return currentEndOffsetForPartition;

    } else {
      return highestDurableOffsetForPartition;
    }
  }

  // The current head offset shouldn't be lower than the highest durable offset. If it is it
  // means that we indexed more data than the current head offset. This is either a bug in the
  // offset handling mechanism or the kafka partition has rolled over. We throw an exception
  // for now, so we can investigate.
  if (currentEndOffsetForPartition < highestDurableOffsetForPartition) {
    final String message =
        String.format(
            "The current head for the partition %d can't "
                + "be lower than the highest durable offset for that partition %d",
            currentEndOffsetForPartition, highestDurableOffsetForPartition);
    LOG.error(message);
    throw new IllegalStateException(message);
  }

  // The head offset for Kafka partition is the offset of the next message to be indexed. We
  // assume that offset is passed into this function. The highest durable offset is the partition
  // offset of the message that is indexed. Hence, the offset is incremented by 1 to get the
  // next message.
  long nextOffsetForPartition = highestDurableOffsetForPartition + 1;

  // Create a recovery task if needed.
  if (currentEndOffsetForPartition - highestDurableOffsetForPartition > maxOffsetDelay) {
    LOG.info(
        "Recovery task needed. The current position {} and head location {} are higher than max"
            + " offset {}",
        highestDurableOffsetForPartition,
        currentEndOffsetForPartition,
        maxOffsetDelay);
    createRecoveryTasks(
        partitionId,
        nextOffsetForPartition,
        currentEndOffsetForPartition - 1,
        maxMessagesPerRecoveryTask);
    return currentEndOffsetForPartition;
  } else {
    LOG.info(
        "The difference between the last indexed position {} and head location {} is lower "
            + "than max offset {}. So, using {} position as the start offset",
        highestDurableOffsetForPartition,
        currentEndOffsetForPartition,
        maxOffsetDelay,
        nextOffsetForPartition);
    return nextOffsetForPartition;
  }
