def load_image_from_string(
    reference: str,
    max_height: Optional[int] = None,
    max_width: Optional[int] = None,
) -> Tuple[str, Optional[float]]:
    """
    Description: This function loads an image from a given reference, which can be a URL, a local file path, or a base64 encoded string.

    Args:
        reference (str): The reference to the image. It can be a URL, a local file path, or a base64 encoded string.
        max_height (Optional[int]): The maximum height to which the image should be resized. If None, the image will not be resized in height.
        max_width (Optional[int]): The maximum width to which the image should be resized. If None, the image will not be resized in width.

    Returns:
        Tuple[str, Optional[float]]: A tuple containing the base64 encoded string of the image and the scaling factor used to resize the image. If the image was not resized, the scaling factor will be None.
    """
    if reference.startswith("http"):
        response = requests.get(reference)
        if response.status_code == 200:
            image = cv2.imdecode(np.frombuffer(response.content, np.uint8), cv2.IMREAD_COLOR)
        else:
            raise InvalidInputFormatError(f"Invalid input format: {reference}")
    elif reference.startswith("data:image"):
        image = cv2.imdecode(np.frombuffer(base64.b64decode(reference.split(",")[1]), np.uint8), cv2.IMREAD_COLOR)
    else:
        try:
            image = cv2.imread(reference)
        except Exception as e:
            raise InvalidInputFormatError(f"Invalid input format: {reference}")

    if max_height is not None or max_width is not None:
        if max_height is not None and max_width is not None:
            image = resize_opencv_image(image, max_height, max_width)
        elif max_height is not None:
            image = resize_opencv_image(image, max_height)
        elif max_width is not None:
            image = resize_opencv_image(image, max_width)

    return numpy_array_to_base64_jpeg(image), None if max_height is None and max_width is None else (max_height / image.shape[0] if max_height is not None else max_width / image.shape[1])
