    feature_processor=None,
    feature_processor_kwargs=None,
    feature_processor_segment_name_mapping=None,
):
    if feature_processor is None:
        feature_processor = filter_zero_weights_feature_processor

    if feature_processor_kwargs is None:
        feature_processor_kwargs = {}

    if feature_processor_segment_name_mapping is None:
        feature_processor_segment_name_mapping = {}

    def _apply_feature_processor(segment_name, segment_data):
        feature_processor_segment_name = feature_processor_segment_name_mapping.get(
            segment_name, segment_name
        )

        if feature_processor is not None:
            segment_data = feature_processor(
                feature_processor_segment_name, segment_data, **feature_processor_kwargs
            )
        return segment_data

    def _add_weights(data, weights):
        return pd.merge(data, weights, left_index=True, right_index=True)

    if segmentation is None:
        # spoof segment name and weights column
        segment_name = None
        weights = pd.DataFrame({"weight": 1}, index=data.index)
        segment_data = _add_weights(data, weights)

        segment_data = _apply_feature_processor(segment_name, segment_data)
        yield segment_name, segment_data
    else:
        for segment_name, segment_weights in segmentation.items():
            weights = segment_weights.to_frame("weight")
            segment_data = _add_weights(data, weights)
            segment_data = _apply_feature_processor(segment_name, segment_data)
            yield segment_name, segment_data

def plot_trend(
    self,
    series=None,
    vline=None,
    colors=["#d4f74f", "#82ab5a", "#ff6c05", "#c12600"],
    title=None,
    start_date=None,
    **kwargs,
):
    # YMAX from PLOT ONLY
    if series is None:
        series = random.choice(self.column_names)
    if title is None:
        title = f"Trend Breakdown for {series}"
    p_indx = self.column_names.get_loc(series)
    cur_trend = self.predicted_trend[series].copy()
    tls = len(cur_trend)
    plot_df = pd.DataFrame(
        {
            'decline_accelerating': cur_trend[
                (
                    np.hstack((np.signbit(self.accel[:, p_indx]), False))
                    & self.slope_sign[:, p_indx]
                )[-tls:]
            ],
            'decline_decelerating': cur_trend[
                (
                    (~np.hstack((np.signbit(self.accel[:, p_indx]), False)))
                    & self.slope_sign[:, p_indx]
                )[-tls:]
            ],
            'growth_decelerating': cur_trend[
                (
                    np.hstack((np.signbit(self.accel[:, p_indx]), False))
                    & (~self.slope_sign[:, p_indx])
                )[-tls:]
            ],
            'growth_accelerating': cur_trend[
                (
                    (~np.hstack((np.signbit(self.accel[:, p_indx]), False)))
                    & (~self.slope_sign[:, p_indx])
                )[-tls:]
            ],
        },
        index=cur_trend.index,
    )
    if start_date == "auto":
        slx = self.forecast_length * 3
        if slx > len(plot_df.index):
            slx = 0
        start_date = plot_df.index[-slx]
    if start_date is not None:
        plot_df = plot_df[plot_df.index >= start_date]
    ax = plot_df.plot(title=title, color=colors, **kwargs)
    handles, labels = ax.get_legend_handles_labels()
    # ax.scatter(cur_trend.index[self.changepoints[:, p_indx]], cur_trend[self.changepoints[:, p_indx]], c='#fdcc09', s=4.0)
    # ax.scatter(cur_trend.index[self.zero_crossings[:, p_indx]], cur_trend[self.zero_crossings[:, p_indx]], c='#512f74', s=4.0)
    if self.trend_anomaly_detector is not None:
        if self.trend_anomaly_detector.output == "univariate":
            i_anom = self.trend_anomaly_detector.anomalies.index[
                self.anomaly_detector.anomalies.iloc[:, 0] == -1
            ]
        else:
            series_anom = self.trend_anomaly_detector.anomalies[series]
            i_anom = series_anom[series_anom == -1].index
        if start_date is not None:
            i_anom = i_anom[i_anom >= start_date]
        i_anom = i_anom[i_anom >= cur_trend.index[0]]
        # only plot if some anomalies, and not way too many anomalies
        if len(i_anom) > 0 and len(i_anom) < len(plot_df) * 0.5:
            scat1 = ax.scatter(
                i_anom.tolist(), cur_trend.loc[i_anom], c="red", s=16.0
            )
            handles += [scat1]
            labels += ['trend anomalies']
    if vline is not None:
        ax.vlines(
            x=vline,
            ls='--',
            lw=1,
            colors='darkred',
            ymin=cur_trend[cur_trend.index >= start_date].min(),
            ymax=cur_trend[cur_trend.index >= start_date].max(),
        )
    ax.legend(handles, labels)
    return ax

def _spatial_matching(
    x,
    y=None,
    n_matches=5,
    metric="euclidean",
    solver=None,
    return_mip=False,
    allow_partial_match=False,
    **metric_kwargs,
):
    try:
        import pulp
    except ImportError as error:
        raise ImportError("spatial matching requires the pulp library") from error
    if metric == "precomputed":
        distance_matrix = x
        match_between = y is not None
    elif y is not None:
        x, x_ids, _ = _validate_geometry_input(
            x, ids=None, valid_geometry_types=_VALID_GEOMETRY_TYPES
        )
        y, y_ids, _ = _validate_geometry_input(
            y, ids=None, valid_geometry_types=_VALID_GEOMETRY_TYPES
        )
        distance_matrix = pairwise_distances(x, y, metric=metric)
        match_between = True
    else:
        x, x_ids, _ = _validate_geometry_input(
            x, ids=None, valid_geometry_types=_VALID_GEOMETRY_TYPES
        )
        y_ids = x_ids
        distance_matrix = pairwise_distances(x, metric=metric, **metric_kwargs)

        match_between = False

    n_targets, n_sources = distance_matrix.shape

    if match_between:
        row, col = numpy.meshgrid(
            numpy.arange(n_targets), numpy.arange(n_sources), indexing="ij"
        )
        row = row.flatten()
        col = col.flatten()
    else:
        # if we are matching within, we need to
        row, col = numpy.triu_indices(
            n=n_targets, m=n_sources, k=int(not match_between)
        )

    mp = pulp.LpProblem("optimal-neargraph", sense=pulp.LpMinimize)
    # a match is as binary decision variable, connecting observation i to observation j
    match_vars = pulp.LpVariable.dicts(
        "match",
        lowBound=0,
        upBound=1,
        indices=zip(row, col, strict=True),
        cat="Continuous" if allow_partial_match else "Binary",
    )
    # we want to minimize the geographic distance of links in the graph
    mp.objective = pulp.lpSum(
        [
            match_vars[i, j] * distance_matrix[i, j]
            for i, j in zip(row, col, strict=True)
        ]
    )

    # for each observation
    for j in range(n_targets):
        # there must be exactly k other matched observations, which might live
        if match_between:
            summand = pulp.lpSum(
                [
                    # over the whole match matrix
                    match_vars[j, i]
                    for i in range(n_sources)
                ]
            )
            sense = 1
        else:
            summand = pulp.lpSum(
                [
                    # in the "upper" triangle, or "lower" triangle
                    match_vars.get((i, j), match_vars.get((j, i)))
                    for i in range(n_sources)
                    if (i != j)
                ]
            )
            sense = int(not allow_partial_match)
        mp += pulp.LpConstraint(summand, sense=sense, rhs=n_matches)
    if match_between:  # but, we may choose to ignore some sources
        for i in range(n_sources):
            summand = pulp.lpSum([match_vars[j, i] for j in range(n_targets)])
            mp += pulp.LpConstraint(summand, sense=-1, rhs=n_matches)

    status = mp.solve(solver)

    if (status != 1) & (not allow_partial_match):
        warnings.warn(
            f"Problem is {pulp.LpStatus[status]}, so edge weights may be non-integer!",
            stacklevel=2,
        )

    edges = [
        (*key, value.value()) for key, value in match_vars.items() if value.value() > 0
    ]
    if not match_between:
        edges.extend([(*tuple(reversed(edge[:-1])), edge[-1]) for edge in edges])

    heads, tails, weights = map(numpy.asarray, zip(*sorted(edges), strict=True))

    if return_mip:
        return x_ids[heads], y_ids[tails], weights, mp
    return x_ids[heads], y_ids[tails], weights

def load(target: Union[str, dict], dictionary: Dictionary, mmap: bool = True) -> "LinkGraph":
    if not isinstance(target, dict):
        if mmap:
            target = joblib.load(target, mmap_mode="r")
        else:
            target = joblib.load(target)

    if target["build_params"]["dictionary"] != dictionary.uuid:
        raise RuntimeError("The specified dictionary is different from the one used to build this link graph")

    indices = target.pop("indices")
    indptr = target.pop("indptr")
    if not isinstance(indices, np.ndarray):
        indices = np.frombuffer(indices, dtype=np.int32)
        indptr = np.frombuffer(indptr, dtype=np.int32)

    return LinkGraph(dictionary, indices, indptr, **target)

def losses(self, predictions, proposals):
    scores, proposal_deltas = predictions

    # parse classification outputs
    gt_classes = (
        cat([p.gt_classes for p in proposals], dim=0) if len(proposals) else torch.empty(0)
    )
    _log_classification_stats(scores, gt_classes)

    # parse box regression outputs
    if len(proposals):
        proposal_boxes = cat([p.proposal_boxes.tensor for p in proposals], dim=0)  # Nx4
        assert not proposal_boxes.requires_grad, "Proposals should not require gradients!"
        # If "gt_boxes" does not exist, the proposals must be all negative and
        # should not be included in regression loss computation.
        # Here we just use proposal_boxes as an arbitrary placeholder because its
        # value won't be used in self.box_reg_loss().
        gt_boxes = cat(
            [(p.gt_boxes if p.has("gt_boxes") else p.proposal_boxes).tensor for p in proposals],
            dim=0,
        )
    else:
        proposal_boxes = gt_boxes = torch.empty((0, 4), device=proposal_deltas.device)

    losses = {
        "loss_cls": cross_entropy(scores, gt_classes, reduction="mean"),
        "loss_box_reg": self.box_reg_loss(
            proposal_boxes, gt_boxes, proposal_deltas, gt_classes
        ),
    }
    return {k: v * self.loss_weight.get(k, 1.0) for k, v in losses.items()}

def eye(N, M=None, k=None, dtype=None):
    dtype = to_torch_dtype(dtype or config.floatx())
    M = N if M is None else M
    k = 0 if k is None else k
    if k == 0:
        # TODO: torch.eye doesn't support bfloat16 with cpu
        if get_device() == "cpu" and dtype == torch.bfloat16:
            return cast(
                torch.eye(
                    N, M, dtype=to_torch_dtype("float32"), device=get_device()
                ),
                dtype,
            )
        return torch.eye(N, M, dtype=dtype, device=get_device())
    diag_length = builtins.max(N, M)
    diag = torch.ones(diag_length, dtype=dtype, device=get_device())
    return torch.diag(diag, diagonal=k)[:N, :M]

def create(
    supply_settings: Callable[[], config.ResilienceSettings] = config.ResilienceSettings, include_post: bool = False
) -> adapters.HTTPAdapter:
    settings = supply_settings()
    allowed_methods = set(util.Retry.DEFAULT_ALLOWED_METHODS)
    if include_post:
        allowed_methods.add("POST")

    retries = util.Retry(
        total=settings.total,
        backoff_factor=settings.backoff_factor,
        status_forcelist=settings.statuses,
        allowed_methods=allowed_methods,
    )
    return adapters.HTTPAdapter(max_retries=retries)

def draw_dataset_dict(self, dic):
    annos = dic.get("annotations", None)
    if annos:
        if "segmentation" in annos[0]:
            masks = [x["segmentation"] for x in annos]
        else:
            masks = None
        if "keypoints" in annos[0]:
            keypts = [x["keypoints"] for x in annos]
            keypts = np.array(keypts).reshape(len(annos), -1, 3)
        else:
            keypts = None

        boxes = [BoxMode.convert(x["bbox"], x["bbox_mode"], BoxMode.XYXY_ABS) for x in annos]

        labels = [x["category_id"] for x in annos]
        colors = None
        if self._instance_mode == ColorMode.SEGMENTATION and self.metadata.get("thing_colors"):
            colors = [
                self._jitter([x / 255 for x in self.metadata.thing_colors[c]]) for c in labels
            ]
        names = self.metadata.get("thing_classes", None)
        if names:
            labels = [names[i] for i in labels]
        labels = [
            "{}".format(i) + ("|crowd" if a.get("iscrowd", 0) else "")
            for i, a in zip(labels, annos)
        ]
        self.overlay_instances(
            labels=labels, boxes=boxes, masks=masks, keypoints=keypts, assigned_colors=colors
        )

    sem_seg = dic.get("sem_seg", None)
    if sem_seg is None and "sem_seg_file_name" in dic:
        with PathManager.open(dic["sem_seg_file_name"], "rb") as f:
            sem_seg = Image.open(f)
            sem_seg = np.asarray(sem_seg, dtype="uint8")
    if sem_seg is not None:
        self.draw_sem_seg(sem_seg, area_threshold=0, alpha=0.5)
    return self.output

def create_app_from_config(config: GatewayConfig) -> GatewayAPI:
    limiter = Limiter(
        key_func=get_remote_address, storage_uri=MLFLOW_GATEWAY_RATE_LIMITS_STORAGE_URI.get()
    )
    app = GatewayAPI(
        config=config,
        limiter=limiter,
        title="MLflow Deployments Server",
        description="The core deployments API for reverse proxy interface using remote inference "
        "endpoints within MLflow",
        version=VERSION,
        docs_url=None,
    )

    @app.get("/", include_in_schema=False)
    async def index():
        return RedirectResponse(url="/docs")

    @app.get("/favicon.ico", include_in_schema=False)
    async def favicon():
        for directory in ["build", "public"]:
            favicon = Path(__file__).parent.parent.parent.joinpath(
                "server", "js", directory, "favicon.ico"
            )
            if favicon.exists():
                return FileResponse(favicon)
        raise HTTPException(status_code=404, detail="favicon.ico not found")

    @app.get("/docs", include_in_schema=False)
    async def docs():
        return get_swagger_ui_html(
            openapi_url="/openapi.json",
            title="MLflow Deployments Server",
            swagger_favicon_url="/favicon.ico",
        )

    @app.get(MLFLOW_DEPLOYMENTS_HEALTH_ENDPOINT)
    # TODO: Remove Gateway server URLs after deprecation window elapses
    @app.get(MLFLOW_GATEWAY_HEALTH_ENDPOINT, include_in_schema=False)
    async def health() -> HealthResponse:
        return {"status": "OK"}

    @app.get(MLFLOW_DEPLOYMENTS_CRUD_ENDPOINT_BASE + "{endpoint_name}")
    async def get_endpoint(endpoint_name: str) -> Endpoint:
        if matched := app.get_dynamic_route(endpoint_name):
            return matched.to_endpoint()

        raise HTTPException(
            status_code=404,
            detail=f"The endpoint '{endpoint_name}' is not present or active on the server. Please "
            "verify the endpoint name.",
        )

    # TODO: Remove Gateway server URLs after deprecation window elapses
    @app.get(MLFLOW_GATEWAY_CRUD_ROUTE_BASE + "{route_name}", include_in_schema=False)
    async def get_route(route_name: str) -> Route:
        if matched := app.get_dynamic_route(route_name):
            return matched

        raise HTTPException(
            status_code=404,
            detail=f"The route '{route_name}' is not present or active on the server. Please "
            "verify the route name.",
        )

    @app.get(MLFLOW_DEPLOYMENTS_CRUD_ENDPOINT_BASE)
    async def list_endpoints(page_token: Optional[str] = None) -> ListEndpointsResponse:
        start_idx = SearchRoutesToken.decode(page_token).index if page_token is not None else 0

        end_idx = start_idx + MLFLOW_DEPLOYMENTS_LIST_ENDPOINTS_PAGE_SIZE
        routes = list(app.dynamic_routes.values())
        result = {"endpoints": [route.to_endpoint() for route in routes[start_idx:end_idx]]}
        if len(routes[end_idx:]) > 0:
            next_page_token = SearchRoutesToken(index=end_idx)
            result["next_page_token"] = next_page_token.encode()

        return result

    # TODO: Remove Gateway server URLs after deprecation window elapses
    @app.get(MLFLOW_GATEWAY_CRUD_ROUTE_BASE, include_in_schema=False)
    async def search_routes(page_token: Optional[str] = None) -> SearchRoutesResponse:
        start_idx = SearchRoutesToken.decode(page_token).index if page_token is not None else 0

        end_idx = start_idx + MLFLOW_GATEWAY_SEARCH_ROUTES_PAGE_SIZE
        routes = list(app.dynamic_routes.values())
        result = {"routes": routes[start_idx:end_idx]}
        if len(routes[end_idx:]) > 0:
            next_page_token = SearchRoutesToken(index=end_idx)
            result["next_page_token"] = next_page_token.encode()

        return result

    @app.get(MLFLOW_DEPLOYMENTS_LIMITS_BASE + "{endpoint}")
    # TODO: Remove Gateway server URLs after deprecation window elapses
    @app.get(MLFLOW_GATEWAY_LIMITS_BASE + "{endpoint}", include_in_schema=False)
    async def get_limits(endpoint: str) -> LimitsConfig:
        raise HTTPException(status_code=501, detail="The get_limits API is not available yet.")

    @app.post(MLFLOW_DEPLOYMENTS_LIMITS_BASE)
    # TODO: Remove Gateway server URLs after deprecation window elapses
    @app.post(MLFLOW_GATEWAY_LIMITS_BASE, include_in_schema=False)
    async def set_limits(payload: SetLimitsModel) -> LimitsConfig:
        raise HTTPException(status_code=501, detail="The set_limits API is not available yet.")

    return app

def _setup_cuda_device(device: torch.device) -> torch.device:
    local_rank = _get_local_rank() or 0
    if device.index is None:
        device = torch.device(type="cuda", index=local_rank)

    # Ensure index is available before setting device
    if device.index >= torch.cuda.device_count():
        raise RuntimeError(
            "The local rank is larger than the number of available GPUs."
        )

    torch.cuda.set_device(device)
    return device

def to_numpy(
    evset: EventSet,
    timestamp_to_datetime: bool = True,
    timestamps: bool = True,
) -> Dict[str, ndarray]:
    timestamp_key = "timestamp"
    index_names = evset.schema.index_names()
    feature_names = evset.schema.feature_names()

    column_names = index_names + feature_names
    if timestamps:
        column_names += [timestamp_key]

    dst = {column_name: [] for column_name in column_names}
    for index, data in evset.data.items():
        assert isinstance(index, tuple)

        if timestamps:
            # Timestamps
            if evset.schema.is_unix_timestamp and timestamp_to_datetime:
                dst[timestamp_key].append(
                    data.timestamps.astype("datetime64[s]")
                )
            else:
                dst[timestamp_key].append(data.timestamps)

        # Features
        for feature_name, feature in zip(feature_names, data.features):
            dst[feature_name].append(feature)

        # Indexes
        num_timestamps = len(data.timestamps)
        for index_name, index_item in zip(index_names, index):
            dst[index_name].append(np.repeat(index_item, num_timestamps))

    dst = {k: np.concatenate(v) for k, v in dst.items()}
    return dst

def _get_user_settings(self, session: HubSession) -> HubUserSettings:
    access_token = session.access_token.get_secret_value()
    token_type = session.token_type
    authorization = f"{token_type.title()} {access_token}"

    response = get(
        url=self._base_url + "/terminal/user",
        headers={"Authorization": authorization},
        timeout=self.TIMEOUT,
    )
    if response.status_code == 200:
        user_settings = response.json()
        filtered = {k: v for k, v in user_settings.items() if v is not None}
        return HubUserSettings.model_validate(filtered)
    status_code = response.status_code
    detail = response.json().get("detail", None)
    raise HTTPException(status_code, detail)

def get_stress(
    self,
    strain: float,
) -> float:
    # create interpolation function
    stress_function = interp1d(
        x=self.strains,
        y=self.stresses,
        kind="linear",
        fill_value="extrapolate",
    )

    return stress_function(strain)

