def get_dtype(
    dtype: Optional[str] = None, device: Optional[torch.device] = None
) -> torch.dtype :
    """
    Description: This function is used to get the data type for a tensor.

    Args:
        dtype (Optional[str]): The data type of the tensor. Defaults to None.
        device (Optional[torch.device]): The device on which the tensor is to be created. Defaults to None.

    Returns:
        torch.dtype: The data type of the tensor.

    Raises:
        ValueError: If the data type is not one of the supported precisions.
        RuntimeError: If the bf16 precision is requested but not available on the hardware.
    """
    if dtype is None:
        if device is None:
            return torch.float32
        else:
            return torch.float32
    elif dtype == "float16":
        if device is None:
            return torch.float16
        else:
            return torch.float16
    elif dtype == "float32":
        if device is None:
            return torch.float32
        else:
            return torch.float32
    elif dtype == "float64":
        if device is None:
            return torch.float64
        else:
            return torch.float64
    elif dtype == "bfloat16":
        if device is None:
            return torch.bfloat16
        else:
            return torch.bfloat16
    else:
        raise ValueError(f"Unsupported data type: {dtype}")
