public SendStatus sendHtmlMail(String subject, List<String> toList, String username, String agentName, String emailHref, String maiContent) {
    SendStatus sendStatus = new SendStatus();
    try {
        // 1. Check if any of the recipients are in the blacklist.
        if (blacklistService.isBlacklisted(toList)) {
            sendStatus.setStatus(SEND_STATUS_FALSE);
            sendStatus.setErrorMessage("One or more recipients are in the blacklist.");
            return sendStatus;
        }

        // 2. Read the HTML template.
        String htmlTemplate = readHtmlTemplate();

        // 3. Replace placeholders with the provided username, agent name, and mail content.
        String htmlContent = htmlTemplate.replace("{{username}}", username).replace("{{agentName}}", agentName).replace("{{emailHref}}", emailHref).replace("{{mailContent}}", maiContent);

        // 4. Set up a mail session.
        Properties properties = new Properties();
        properties.put("mail.smtp.host", host);
        properties.put("mail.smtp.port", port);
        properties.put("mail.smtp.auth", "true");
        properties.put("mail.smtp.starttls.enable", "true");
        properties.put("mail.smtp.ssl.trust", host);
        Session session = Session.getInstance(properties, new Authenticator() {
            @Override
            protected PasswordAuthentication getPasswordAuthentication() {
                return new PasswordAuthentication(user, password);
            }
        });

        // 5. Create a MimeMessage and set the required properties.
        MimeMessage message = new MimeMessage(session);
        message.setFrom(new InternetAddress(sendAddress));
        message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(String.join(",", toList)));
        message.setSubject(subject);
        message.setContent(htmlContent, "text/html; charset=utf-8");

        // 6. Send the email.
        Transport.send(message);

        // 7. Set the status of the SendStatus to true if the email is sent successfully.
        sendStatus.setStatus("true");
    } catch (MessagingException e) {
        // 8. Log the error and set the error message of the SendStatus.
        log.error("Error sending email: {}", e.getMessage());
        sendStatus.setStatus(SEND_STATUS_FALSE);
        sendStatus.setErrorMessage(e.getMessage());
    } finally {
        // 9. Close the transport if it was opened.
        if (session != null && session.getTransport() != null && session.getTransport().isConnected()) {
            try {
                session.getTransport().close();
            } catch (MessagingException e) {
                log.error("Error closing transport: {}", e.getMessage());
            }
        }
    }

    // 10. Return the SendStatus object.
    return sendStatus;
}
