void validateRoleDuplicate(String name, String code, Long id) {
    // 1. 是否存在相同名字的角色
    RoleDO roleByName = roleMapper.selectOne(new QueryWrapper<RoleDO>()
            .eq(RoleDO::getName, name)
            .ne(RoleDO::getId, id));
    if (ObjectUtil.isNotNull(roleByName)) {
        throw exception(ROLE_NAME_DUPLICATE_ERROR);
    }
    // 2. 是否存在相同编码的角色
    RoleDO roleByCode = roleMapper.selectOne(new QueryWrapper<RoleDO>()
            .eq(RoleDO::getCode, code)
            .ne(RoleDO::getId, id));
    if (ObjectUtil.isNotNull(roleByCode)) {
        throw exception(ROLE_CODE_DUPLICATE_ERROR);
    }
    // 3. 校验是否是超级管理员
    if (RoleCodeEnum.isSuperAdmin(code)) {
        throw exception(ROLE_CODE_SUPER_ADMIN_ERROR);
    }
}
