public void add(List<Document> documents) {
	if (CollectionUtils.isEmpty(documents)) {
		return;
	}

	List<SearchDocument> searchDocuments = documents.stream()
			.map(document -> {
				String id = document.getId();
				String content = document.getContent();
				Map<String, Object> metadata = document.getMetadata();
				Map<String, Object> filterableMetadata = filterMetadataFields.stream()
						.collect(Collectors.toMap(MetadataField::name,
								metadataField -> metadata.get(metadataField.name())));
				byte[] embedding = document.getEmbedding();
				return new SearchDocument(id, content, embedding, metadata, filterableMetadata);
			})
			.collect(Collectors.toList());

	IndexDocumentsResult indexDocumentsResult = searchClient.indexDocuments(searchDocuments);
	List<IndexingResult> indexingResults = indexDocumentsResult.getResults();
	if (indexingResults.stream().anyMatch(IndexingResult::isFailed)) {
		throw new IllegalArgumentException("Failed to index documents.");
	}
}
