public void put(String path, long position, ByteBuf data) {
    writeLock.lock();
    try {
        NavigableMap<Long, Value> cache = path2cache.computeIfAbsent(path, k -> new TreeMap<>());
        Value floorEntry = cache.floorEntry(position).getValue();
        if (floorEntry == null || position > floorEntry.end) {
            Value newEntry = new Value(position, data.readableBytes());
            cache.put(position, newEntry);
            lru.put(new Key(path, position), newEntry);
        }
        ensureCapacity(cache);
        Value value = cache.get(position);
        value.blocks.set(value.blocks.nextClearBit(0));
        value.dataLength += data.readableBytes();
        cache.put(position, value);
        write(value);
    } finally {
        writeLock.unlock();
    }
}
