@Override
public Optional<Long> getDistinctCountFor(String fieldName) throws IOException {
  // Create a SearchRequest to retrieve the distinct count for the given field.
  SearchRequest searchRequest =
      createSearchRequest(
          processIndex.getIndexName(),
          processIndex.getTypeName(),
          new SearchSourceBuilder()
              .aggregation(
                  AggregationBuilders.cardinality(DISTINCT_FIELD_COUNTS)
                      .field(fieldName)
                      .precisionThreshold(1000000)));

  // Execute the SearchRequest and retrieve the SearchResponse.
  SearchResponse searchResponse =
      esClient.search(searchRequest, RequestOptions.DEFAULT);

  // Retrieve the Aggregations from the SearchResponse.
  Aggregations aggregations = searchResponse.getAggregations();

  // Check if the Aggregations contain the "distinctFieldCounts" aggregation.
  if (aggregations.get(DISTINCT_FIELD_COUNTS) != null) {
    // Retrieve the Cardinality aggregation from the Aggregations.
    Cardinality cardinalityAggregation = aggregations.get(DISTINCT_FIELD_COUNTS);

    // Return the cardinality value from the Cardinality aggregation.
    return Optional.of(cardinalityAggregation.getValue());
  }

  // If the Aggregations do not contain the "distinctFieldCounts" aggregation, return an empty Optional.
  return Optional.empty();
}
