public String sign(Map<String, Object> map) {
    // Create a JWTCreator object
    JWTCreator.Builder builder = JWT.create();

    // Add the claims to the JWT
    for (Map.Entry<String, Object> entry : map.entrySet()) {
        builder.withClaim(entry.getKey(), entry.getValue());
    }

    // Set the expiration time of the token
    long expirationTime = System.currentTimeMillis() + jwtProperties.getExpirationTime();
    builder.withExpiresAt(new Date(expirationTime));

    // Set the issuer of the token
    builder.withIssuer(jwtProperties.getIssuer());

    // Set the audience of the token
    builder.withAudience(jwtProperties.getAudience());

    // Set the subject of the token
    builder.withSubject(jwtProperties.getSubject());

    // Set the JWT ID
    builder.withJWTId(UUID.randomUUID().toString());

    // Set the JWT type
    builder.withType(jwtProperties.getType());

    // Set the JWT algorithm
    Algorithm algorithm = Algorithm.HMAC256(jwtProperties.getTokenSecret());

    // Sign the JWT with the provided algorithm
    String signedToken = builder.sign(algorithm);

    // Return the signed token
    return signedToken;
}
