    if (jobStats.getStatus() == RebalanceResult.Status.DONE || jobStats.getStatus() == RebalanceResult.Status.NO_OP) {
      continue;
    }
    if (jobStats.getStatus() == RebalanceResult.Status.IN_PROGRESS) {
      // If the job is still running, we can't retry it.
      continue;
    }
    if (jobStats.getStatus() == RebalanceResult.Status.FAILED) {
      // If the job has failed, we can retry it.
      TableRebalanceContext jobCtx = TableRebalanceContext.forRetry(jobId, jobStats.getConfig(), 1);
      candidateJobs.computeIfAbsent(jobId, k -> new HashSet<>()).add(Pair.of(jobCtx, jobStats.getStartTimeMs()));
    }
  }
  return candidateJobs;
}
