public static void unTarZip(String tarFilePath, String targetDirectoryPath) {
    // Check if the target directory is a file or not a directory
    File targetDirectory = new File(targetDirectoryPath);
    if (targetDirectory.isFile()) {
        throw new CoreException(PDKRunnerErrorCodes.CLI_UNZIP_DIR_IS_FILE, "Unzip director is a file, expect to be directory or none");
    }

    // Create the target directory if it doesn't exist
    if (!targetDirectory.exists()) {
        targetDirectory.mkdirs();
    }

    // Open the tar file
    try (TarArchiveInputStream tarInputStream = new TarArchiveInputStream(new GzipCompressorInputStream(new FileInputStream(tarFilePath)))) {
        TarArchiveEntry entry;
        while ((entry = tarInputStream.getNextTarEntry()) != null) {
            // Check if the entry is a directory
            if (entry.isDirectory()) {
                // Create the directory if it doesn't exist
                File directory = new File(targetDirectoryPath, entry.getName());
                if (!directory.exists()) {
                    directory.mkdirs();
                }
            } else {
                // Create the parent directories if they don't exist
                File parentDirectory = new File(targetDirectoryPath, entry.getName()).getParentFile();
                if (!parentDirectory.exists()) {
                    parentDirectory.mkdirs();
                }

                // Write the file to the target directory
                try (OutputStream outputStream = new FileOutputStream(new File(targetDirectoryPath, entry.getName()))) {
                    IOUtils.copy(tarInputStream, outputStream);
                }
            }
        }
    } catch (IOException e) {
        throw new CoreException(PDKRunnerErrorCodes.COMMON_IO_EXCEPTION, "Error while decompressing tar.gz file");
