public void submit(@NonNull PartitionPlanConfig partitionPlanConfig) throws SchedulerException, ClassNotFoundException {
    // Disable all related partition plan tasks
    List<Long> flowInstanceIds = this.partitionPlanRepository.findByDatabaseIdAndEnabled(partitionPlanConfig.getDatabaseId(), true)
            .stream().map(PartitionPlanEntity::getFlowInstanceId).collect(Collectors.toList());
    this.flowInstanceService.disable(flowInstanceIds);

    // Save the configuration
    PartitionPlanEntity partitionPlanEntity = new PartitionPlanEntity();
    partitionPlanEntity.setDatabaseId(partitionPlanConfig.getDatabaseId());
    partitionPlanEntity.setParameters(JsonUtils.toJson(partitionPlanConfig));
    partitionPlanEntity.setEnabled(true);
    this.partitionPlanRepository.save(partitionPlanEntity);

    // Create and enable schedules for partition plan table creation and dropping
    if (CollectionUtils.isNotEmpty(partitionPlanConfig.getPartitionTableConfigs())
            && partitionPlanConfig.getPartitionTableConfigs().stream().anyMatch(PartitionPlanTableConfig::isEnabled)) {
        List<Long> partitionPlanTableIds = partitionPlanConfig.getPartitionTableConfigs().stream()
                .map(PartitionPlanTableConfig::getId).collect(Collectors.toList());
        List<PartitionPlanTableEntity> partitionPlanTableEntities = this.partitionPlanTableRepository
                .findByIdIn(partitionPlanTableIds);
        List<Long> scheduleIds = partitionPlanTableEntities.stream().map(PartitionPlanTableEntity::getScheduleId)
                .collect(Collectors.toList());
        this.scheduleService.disable(scheduleIds);
        List<ScheduleEntity> scheduleEntities = this.scheduleService.create(partitionPlanTableEntities.stream()
                .map(this::entityToModel).collect(Collectors.toList()));
        scheduleEntities.forEach(scheduleEntity -> {
            ScheduleEntity schedule = this.scheduleRepository.save(scheduleEntity);
            PartitionPlanTableEntity partitionPlanTableEntity = partitionPlanTableEntities.stream()
                    .filter(ppt -> ppt.getScheduleId().equals(schedule.getId())).findFirst().get();
            partitionPlanTableEntity.setScheduleId(schedule.getId());
            this.partitionPlanTableRepository.save(partitionPlanTableEntity);
        });
    }
