  return HttpResponse.of(
      HttpStatus.OK,
      MediaType.JSON,
      JsonUtil.writeAsString(
          ImmutableMap.of(
              indexName.orElseThrow(),
              ImmutableMap.of("mappings", ImmutableMap.of("properties", propertiesMap)))));
