 */
public static CacheControlHeader fromExpirationTime(String expirationTime) {
  if (expirationTime == null) {
    logger.atWarning().log("Null expiration time string provided. Using default max age.");
    return getDefaultInstance();
  }

  // Split the expiration time string into a list of time units.
  List<String> timeUnits = Splitter.on(Pattern.compile("\\s+")).splitToList(expirationTime);

  // Initialize the expiration time in seconds.
  long expirationTimeSeconds = 0;

  // Iterate over the time units and convert each unit to seconds.
  for (String timeUnit : timeUnits) {
    // Extract the number and unit from the time unit string.
    String numberString = timeUnit.replaceAll("[^0-9]", "");
    String unitString = timeUnit.replaceAll("[0-9]", "");

    // Check if the unit string is valid.
    if (!EXPIRATION_TIME_UNITS.containsKey(unitString)) {
      logger.atWarning().log("Invalid time unit '%s' provided. Using default max age.", unitString);
      return getDefaultInstance();
    }

    // Convert the number and unit to seconds.
    long number = Long.parseLong(numberString);
    TemporalUnit unit = EXPIRATION_TIME_UNITS.get(unitString);
    long unitSeconds = unit.getDuration().getSeconds();
    long unitTimeSeconds = number * unitSeconds;

    // Add the unit time to the expiration time.
    expirationTimeSeconds += unitTimeSeconds;
  }

  // Check if the expiration time is valid.
  if (expirationTimeSeconds <= 0) {
    logger.atWarning().log("Invalid expiration time '%s' provided. Using default max age.", expirationTime);
    return getDefaultInstance();
