private LazyBlockList tryFetch(BlockListType blockListType) {
    // TODO: use more informative exceptions to describe retriable errors
    return retrier.callWithRetry(
        () -> {
            String url = blockListUrls.get(blockListType.name());
            if (url == null) {
                throw new BsaException("Block list URL not found for type " + blockListType.name());
            }
            try {
                URL blockListUrl = new URL(url);
                HttpsURLConnection connection = (HttpsURLConnection) urlConnectionService.openConnection(blockListUrl);
                connection.setRequestMethod(HttpMethods.GET);
                connection.setDoOutput(false);
                connection.setDoInput(true);
                connection.setUseCaches(false);
                connection.setConnectTimeout(5000);
                connection.setReadTimeout(5000);
                connection.setRequestProperty("Authorization", "Bearer " + credential.getAccessToken());
                connection.setRequestProperty("Content-Type", "application/json");
                connection.setRequestProperty("Accept", "application/json");
                connection.connect();
                int responseCode = connection.getResponseCode();
                if (responseCode != SC_OK) {
                    String responseMessage = connection.getResponseMessage();
                    throw new BsaException("Failed to fetch block list data: " + responseCode + " " + responseMessage);
                }
                InputStream inputStream = new BufferedInputStream(connection.getInputStream());
                byte[] blockListData = ByteStreams.toByteArray(inputStream);
                return new LazyBlockList(blockListData);
            } catch (IOException | GeneralSecurityException e) {
                throw new BsaException("Failed to fetch block list data: " + e.getMessage(), e);
            }
        },
        e -> e instanceof BsaException && ((BsaException) e).isRetriable());
