    this.prometheusMeterRegistry = prometheusMeterRegistry;
    this.astraConfig = astraConfig;
    this.s3Client = s3Client;
    Metrics.addRegistry(prometheusMeterRegistry);
    LOG.info("Started Astra process with config: {}", astraConfig);
