public long determineStartingOffset(
      long currentEndOffsetForPartition,
      long currentBeginningOffsetForPartition,
      AstraConfigs.IndexerConfig indexerConfig) {
  // Get the latest snapshot for the partition.
  List<SnapshotMetadata> snapshots = snapshotMetadataStore.getLatestSnapshotsForPartition(partitionId);

  // Get the latest recovery task for the partition.
  List<RecoveryTaskMetadata> recoveryTasks = recoveryTaskMetadataStore.getLatestRecoveryTasksForPartition(partitionId);

  // Delete stale live snapshots.
  List<SnapshotMetadata> staleSnapshots = deleteStaleLiveSnapshots(snapshots);

  // Get the highest durable offset for the partition.
  long highestDurableOffset = getHighestDurableOffsetForPartition(snapshots, recoveryTasks, partitionId);

  // If the highest durable offset is less than or equal to 0, check the indexer configuration to determine the starting offset.
  if (highestDurableOffset <= 0) {
    if (indexerConfig.readFromLocationOnStart == AstraConfigs.IndexerConfig.ReadFromLocationOnStart.LATEST) {
      // If the indexer is configured to read from the latest offset, return the latest offset.
      return currentEndOffsetForPartition;
    } else if (indexerConfig.readFromLocationOnStart == AstraConfigs.IndexerConfig.ReadFromLocationOnStart.EARLIEST) {
      // If the indexer is configured to read from the earliest offset, return -1.
      return -1;
    } else {
      // If the indexer is configured to read from the beginning offset, return the beginning offset.
      return currentBeginningOffsetForPartition;
    }
  }

  // If the current end offset for the partition is lower than the highest durable offset, throw an exception.
  if (currentEndOffsetForPartition < highestDurableOffset) {
    throw new IllegalStateException("Current end offset for partition is lower than the highest durable offset for the partition.");
  }

  // The next offset for the partition is calculated by incrementing the highest durable offset by 1.
  long nextOffsetForPartition = highestDurableOffset + 1;

  // If the difference between the last indexed position and the head location is higher than the max offset, create a recovery task.
  if (currentEndOffsetForPartition - currentBeginningOffsetForPartition > maxOffsetDelay) {
    // Create a recovery task.
    String recoveryTaskName = getRecoveryTaskName(partitionId);
    RecoveryTaskMetadata recoveryTaskMetadata = new RecoveryTaskMetadata(recoveryTaskName, partitionId, nextOffsetForPartition, currentEndOffsetForPartition);
    recoveryTaskMetadataStore.createRecoveryTask(recoveryTaskMetadata);

    // Increment the recovery tasks created counter.
    recoveryTasksCreated.increment();

    // Return the next offset for the partition.
    return nextOffsetForPartition;
  } else {
    // Return the next offset for the partition.
    return nextOffsetForPartition;
  }
}
