public SendStatus sendHtmlMail(String subject, List<String> toList, String username, String agentName, String emailHref, String maiContent) throws MessagingException {
    // Check if any of the recipients are in the blacklist
    if (CollectionUtils.isEmpty(toList)) {
        return new SendStatus(false, "No recipients provided");
    }

    // Read the HTML template
    String htmlTemplate = Jsoup.parse(emailHref).html();

    // Replace placeholders with the provided username, agent name, and mail content
    String htmlContent = htmlTemplate.replace("{username}", username).replace("{agentName}", agentName).replace("{maiContent}", maiContent);

    // Set up a mail session
    Properties props = new Properties();
    props.put("mail.smtp.host", "smtp.gmail.com");
    props.put("mail.smtp.port", "587");
    props.put("mail.smtp.auth", "true");
    props.put("mail.smtp.starttls.enable", "true");
    Session session = Session.getInstance(props, new javax.mail.Authenticator() {
        protected PasswordAuthentication getPasswordAuthentication() {
            return new PasswordAuthentication("your_email@gmail.com", "your_password");
        }
    });

    // Create a MimeMessage
    MimeMessage message = new MimeMessage(session);
    message.setFrom(new InternetAddress("your_email@gmail.com"));
    message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(StringUtils.join(toList, ",")));
    message.setSubject(subject);
    message.setContent(htmlContent, "text/html");

    // Send the email
    Transport.send(message);

    // Return a SendStatus with a true status
    return new SendStatus(true, "Email sent successfully");
}
