public Map<String, Set<String>> getGroupedCasesByMolecularProfileId(List<String> molecularProfileIds, List<String> caseIds) {
    Map<String, Set<String>> result = new HashMap<>();

    if (CollectionUtils.isEmpty(caseIds)) {
        for (String molecularProfileId : molecularProfileIds) {
            result.put(molecularProfileId, new HashSet<>());
        }
    } else {
        for (String caseId : caseIds) {
            String molecularProfileId = getMolecularProfileId(caseId);
            Set<String> caseIdsForMolecularProfileId = result.get(molecularProfileId);
            if (caseIdsForMolecularProfileId == null) {
                caseIdsForMolecularProfileId = new HashSet<>();
                result.put(molecularProfileId, caseIdsForMolecularProfileId);
            }
            caseIdsForMolecularProfileId.add(caseId);
        }
    }

    return result;
}
