public static List<TableCheckVo> checkTablesWithFilter(Endpoint endpoint, String nameFilter) {
    // Create a new list to store the TableCheckVo objects
    List<TableCheckVo> checkedTables = Lists.newLinkedList();

    // Get the list of tables from the endpoint
    List<Table> tables = endpoint.getTables();

    // Iterate over the tables and check if the name matches the filter
    for (Table table : tables) {
        if (StringUtils.isEmpty(nameFilter) || table.getName().matches(nameFilter)) {
            // Create a new TableCheckVo object and add it to the list
            TableCheckVo tableCheckVo = new TableCheckVo(table);
            checkedTables.add(tableCheckVo);
        }
    }

    // Return the list of checked tables
    return checkedTables;
}
