public static String getExpandUrl(String url, Object paramObj) {
    // Check if the parameter object is a Map
    if (paramObj instanceof Map) {
        // If it is a Map, iterate over its entries and append them as query parameters
        Map<String, String> queryParams = (Map<String, String>) paramObj;
        for (Map.Entry<String, String> entry : queryParams.entrySet()) {
            url = UriComponentsBuilder.fromHttpUrl(url).queryParam(entry.getKey(), entry.getValue()).toUriString();
        }
    } else {
        // If it is not a Map, retrieve the values of all fields in the object and treat them as query parameters
        for (Field field : paramObj.getClass().getDeclaredFields()) {
            field.setAccessible(true);
            String fieldName = field.getName();
            Object fieldValue = field.get(paramObj);
            url = UriComponentsBuilder.fromHttpUrl(url).queryParam(fieldName, fieldValue).toUriString();
        }
    }
    return url;
}
