           .withRegion(Regions.US_EAST_1)
           .build();

    // Create an S3 object
    S3Object s3Object = new S3Object();
    s3Object.setBucketName(bucketName);
    s3Object.setKey(key);
    s3Object.setInputStream(inputStream);

    // Set metadata if provided
    if (objectMetadata!= null) {
        s3Object.setObjectMetadata(objectMetadata);
    }

    // Upload the object to S3
    PutObjectResult putObjectResult = s3Client.putObject(s3Object);

    // Create an S3Resource object
    S3Resource s3Resource = new S3Resource();
    s3Resource.setBucketName(bucketName);
    s3Resource.setKey(key);
    s3Resource.setObjectMetadata(objectMetadata);
    s3Resource.setEtag(putObjectResult.getETag());

    return s3Resource;
}
