                builder.setCpu(parseCpuMetrics(responseBody));
                break;
            case "memory":
                builder.setMemory(parseMemoryMetrics(responseBody));
                break;
            case "disk":
                builder.setDisk(parseDiskMetrics(responseBody));
                break;
            default:
                throw new IllegalArgumentException("Invalid metrics name");
        }

        // Set response code to SUCCESS
        builder.setResponseCode(ResponseCode.SUCCESS);
    } catch (IOException e) {
        // Set response code to FAIL and include exception message in response
        builder.setResponseCode(ResponseCode.FAIL);
        builder.setResponseMessage(e.getMessage());
    } finally {
        // Abort HTTP request if it was created successfully
        if (request!= null) {
            request.abort();
        }
    }
