public static URI resolve(URI base, URI ref) {
    // Check if the reference URI has a non-empty path
    if (ref.getPath()!= null &&!ref.getPath().isEmpty()) {
        // Check if the base URI's path ends with a slash
        if (!base.getPath().endsWith("/")) {
            // Create a new URI with the base URI's path appended with a slash
            try {
                base = new URI(base.getScheme(), base.getAuthority(), base.getPath() + "/", null, null);
            } catch (URISyntaxException e) {
                throw new IllegalStateException("Error creating new URI", e);
            }
        }
    }
    // Return the resolved URI
    return URIUtils.resolve(base, ref);
}
