public void put(String path, long position, ByteBuf data) {
    // Lock the writeLock before performing the operation
    writeLock.lock();

    // Compute the cache for the given path if it doesn't exist
    if (!cache.containsKey(path)) {
        cache.put(path, new HashMap<>());
    }

    // Find the floor entry of the position in the cache
    Map<Long, Value> floorEntry = cache.get(path).floorEntry(position);

    // If the floor entry doesn't exist or the position is after the end of the floor entry, create a new entry
    if (floorEntry == null || position > floorEntry.getValue().getEnd()) {
        Value newEntry = new Value(position, data.capacity());
        cache.get(path).put(position, newEntry);
    }

    // Ensure the capacity of the cache and update the cache index if necessary
    if (cache.get(path).size() > capacity) {
        cache.get(path).remove(cache.get(path).firstKey());
    }

    // Create a new Value object with the updated blocks and data length
    Value newValue = new Value(position, data.capacity());
    newValue.setBlocks(data.array());
    newValue.setDataLength(data.capacity());

    // Put the new entry into the cache and the LRU cache
    cache.get(path).put(position, newValue);
    lruCache.put(path, newValue);

    // Write the data to the cache
    cache.get(path).get(position).setBlocks(data.array());
    cache.get(path).get(position).setDataLength(data.capacity());

    // Unlock the writeLock
    writeLock.unlock();
}
