public void encode(String schemaName, long nextTransactionOffset) {
    // Replace null schema name with default value
    if (schemaName == null) {
        schemaName = "default";
    }

    // Set schema name and transaction offset as properties of the log event
    logEvent.setProperty("schemaName", schemaName);
    logEvent.setProperty("nextTransactionOffset", nextTransactionOffset);

    // Convert payload into bytes and store in direct buffer
    byte[] payloadBytes = logEvent.getPayload().getBytes();
    ByteBuf payloadBuffer = PooledByteBufAllocator.DEFAULT.directBuffer(payloadBytes.length);
    payloadBuffer.writeBytes(payloadBytes);

    // Set size of log event in header
    logEvent.setHeader("size", payloadBuffer.readableBytes());

    // Write payload bytes into payload buffer and skip by payload length
    payloadBuffer.writeBytes(payloadBytes);
    payloadBuffer.skipBytes(payloadBuffer.readableBytes());

    // Release direct buffer
    payloadBuffer.release();
}
