public ListenableFuture<Void> queue(ExecuteEntry executeEntry, Poller poller, Duration timeout) {
    ExecuteOperationMetadata metadata = new ExecuteOperationMetadata(executeEntry);
    Operation operation = new Operation(metadata);

    if (cache.contains(operation)) {
        poller.pause();
        log.info("Cached result found for actionKey {}", operation.getActionKey());
        return Futures.immediateFuture(null);
    }

    Stopwatch stopwatch = Stopwatch.createStarted();
    poller.queue(executeEntry, operation, stopwatch);
    return Futures.transform(poller.getFuture(), (result) -> {
        log.info("Operation queued with result {}", result);
        return null;
    }, MoreExecutors.directExecutor());
}
