@Override
public Map<ProcessKey, List<ProcessEntity>> getProcessesGrouped(String tenantId, @Nullable Set<String> allowedBPMNProcessIds) {
    // Create a new Elasticsearch aggregation query
    SearchSourceBuilder searchSourceBuilder = new SearchSourceBuilder();
    searchSourceBuilder.query(QueryBuilders.matchQuery("tenantId", tenantId));
    searchSourceBuilder.aggregation(AggregationBuilders.terms("processId").field("processId"));
    searchSourceBuilder.aggregation(AggregationBuilders.topHits("version").sort("version", SortOrder.DESC));

    // Execute the query and get the aggregation results
    SearchResponse searchResponse = client.search(searchSourceBuilder);
    Aggregations aggregations = searchResponse.getAggregations();

    // Create a map to store the results
    Map<ProcessKey, List<ProcessEntity>> results = new HashMap<>();

    // Iterate over the aggregation results and add the processes to the map
    for (Terms.Bucket bucket : aggregations.get("processId").getBuckets()) {
        String processId = bucket.getKeyAsString();
        List<ProcessEntity> processes = new ArrayList<>();
        for (SearchHit hit : bucket.getAggregations().get("version").getHits().getHits()) {
            ProcessEntity process = new ProcessEntity();
            process.setProcessId(processId);
            process.setVersion(hit.getSourceAsMap().get("version"));
            processes.add(process);
        }
        results.put(new ProcessKey(tenantId, processId), processes);
    }

    return results;
}
