@Override
public <T> T clone(T object) {
    if (object instanceof String) {
        return object;
    } else if (object instanceof Collection) {
        Collection<T> collection = (Collection<T>) object;
        T firstNonNullElement = null;
        for (T element : collection) {
            if (element!= null) {
                firstNonNullElement = element;
                break;
            }
        }
        if (firstNonNullElement!= null &&!(firstNonNullElement instanceof Serializable)) {
            return objectMapper.clone(collection);
        }
    } else if (object instanceof Map) {
        Map<T, T> map = (Map<T, T>) object;
        T firstNonNullEntry = null;
        for (Map.Entry<T, T> entry : map.entrySet()) {
            if (entry.getKey()!= null || entry.getValue()!= null) {
                firstNonNullEntry = entry;
                break;
            }
        }
        if (firstNonNullEntry!= null && (!(firstNonNullEntry.getKey() instanceof Serializable) ||!(firstNonNullEntry.getValue() instanceof Serializable))) {
            return objectMapper.clone(map);
        }
    } else if (object instanceof JsonNode) {
        return ((JsonNode) object).deepCopy();
    } else if (object instanceof Serializable) {
        return serializationHelper.clone(object);
    } else {
        return jsonMarshalingStrategy.clone(object);
    }
}
