public String sign(Map<String, Object> map) {
    // Generate a JWT token with the provided map of claims
    JwtBuilder builder = Jwts.builder();
    builder.setClaims(map);
    String token = builder.compact();

    // Sign the JWT token with the HMAC SHA-256 algorithm
    Mac mac = Mac.getInstance("HmacSHA256");
    mac.init(new SecretKeySpec(token.getBytes(), "HmacSHA256"));
    String signedToken = Base64.getEncoder().encodeToString(mac.doFinal());

    // Return the signed JWT token as a string
    return signedToken;
}
