public static Map<String, Set<Pair<TableRebalanceContext, Long>>> getCandidateJobs(String tableNameWithType, Map<String, Map<String, String>> allJobMetadata) throws Exception {
    Map<String, Set<Pair<TableRebalanceContext, Long>>> candidateJobs = new HashMap<>();

    for (Map.Entry<String, Map<String, String>> jobMetadata : allJobMetadata.entrySet()) {
        String jobId = jobMetadata.getKey();
        Map<String, String> metadata = jobMetadata.getValue();

        if (metadata.get("status").equals("completed") || metadata.get("status").equals("running") || metadata.get("status").equals("cancelled")) {
            continue;
        }

        if (metadata.get("status").equals("stuck")) {
            long startTime = Long.parseLong(metadata.get("startTime"));
            TableRebalanceContext context = new TableRebalanceContext(tableNameWithType, jobId, startTime);
            Set<Pair<TableRebalanceContext, Long>> contexts = candidateJobs.getOrDefault(jobId, new HashSet<>());
            contexts.add(new Pair<>(context, startTime));
            candidateJobs.put(jobId, contexts);
        }
    }

    return candidateJobs;
}
