@Override
public CompletableFuture<RemotingCommand> sendMessageBack(ProxyContext ctx, ReceiptHandle handle, String messageId,
        ConsumerSendMsgBackRequestHeader requestHeader, long timeoutMillis) {
    // Check if delay level and offset are null
    if (requestHeader.getDelayLevel() == null || requestHeader.getOffset() == null) {
        RemotingCommand response = new RemotingCommand();
        response.setCode(ResponseCode.DELAY_LEVEL_OR_OFFSET_NULL);
        response.setRemark("Delay level or offset is null");
        return CompletableFuture.completedFuture(response);
    }

    // Retrieve topic and consumer group from request header
    String topic = requestHeader.getTopic();
    String consumerGroup = requestHeader.getConsumerGroup();

    // Check if topic id in request header matches topic id in message queue
    if (!topic.equals(ctx.getTopic())) {
        log.error("Topic id in request header does not match topic id in message queue");
        throw new ProxyException("Topic id in request header does not match topic id in message queue");
    }

    // Pull message from store based on group id, topic id, and offset
    Message message = ctx.getMessageStore().getMessage(consumerGroup, topic, requestHeader.getOffset());

    // Check if message is found
    if (message == null) {
        RemotingCommand response = new RemotingCommand();
        response.setCode(ResponseCode.MESSAGE_NOT_FOUND);
        response.setRemark("Message not found");
        return CompletableFuture.completedFuture(response);
    }

    // Check if delivery attempts exceed maximum delivery attempt
    if (message.getDeliveryAttempts() >= ctx.getMaxDeliveryAttempts()) {
        // Send message to dead letter service
        ctx.getDeadLetterService().sendMessage(message);
        RemotingCommand response = new RemotingCommand();
        response.setCode(ResponseCode.MESSAGE_DELIVERY_ATTEMPTS_EXCEEDED);
        response.setRemark("Message delivery attempts exceeded");
        return CompletableFuture.completedFuture(response);
    }

    // Apply message consume retry strategy based on delay level
    if (requestHeader.getDelayLevel() < 0) {
        // Send message to dead letter service
        ctx.getDeadLetterService().sendMessage(message);
        RemotingCommand response = new RemotingCommand();
        response.setCode(ResponseCode.MESSAGE_DELAY_LEVEL_NEGATIVE);
        response.setRemark("Message delay level is negative");
        return CompletableFuture.completedFuture(response);
    } else if (requestHeader.getDelayLevel() == 0) {
        // Calculate delivery attempts and set original queue offset
        int deliveryAttempts = message.getDeliveryAttempts() + 1;
        long originalQueueOffset = message.getOffset();
        message.setDeliveryAttempts(deliveryAttempts);
        message.setOriginalQueueOffset(originalQueueOffset);
    } else {
        // Set delivery attempts and original queue offset
        int deliveryAttempts = message.getDeliveryAttempts() + 1;
        long originalQueueOffset = message.getOffset();
        message.setDeliveryAttempts(deliveryAttempts);
        message.setOriginalQueueOffset(originalQueueOffset);
    }

    // Set response code and remark
    RemotingCommand response = new RemotingCommand();
    response.setCode(ResponseCode.SUCCESS);
    response.setRemark("Message sent back successfully");

    // Return response
    return CompletableFuture.completedFuture(response);
}
