public static byte[] serialize(Set<ByteArrayWrapper> set) {
    // Check if the input set is empty
    if (CollectionUtils.isEmpty(set)) {
        return null;
    }

    // Initialize a new byte array with the size of the input set
    byte[] serializedArray = new byte[set.size()];

    // Iterate through the input set and serialize each ByteArrayWrapper object
    int index = 0;
    for (ByteArrayWrapper wrapper : set) {
        serializedArray[index] = wrapper.getByteArray();
        index++;
    }

    return serializedArray;
}
