@Override
public void collect(CollectRep.MetricsData.Builder builder, long monitorId, String app, Metrics metrics) {
    try {
        // Check the DNS protocol parameter
        String protocol = metrics.getDnsProtocol();
        if (protocol == null || protocol.isEmpty()) {
            throw new IllegalArgumentException("DNS protocol parameter is missing");
        }

        // Run the dig command
        String command = "dig " + protocol + " " + app + " " + monitorId;
        Process process = Runtime.getRuntime().exec(command);
        process.waitFor();

        // Build the DNS metrics data
        CollectRep.MetricsData.DnsMetrics.Builder dnsBuilder = CollectRep.MetricsData.DnsMetrics.newBuilder();
        dnsBuilder.setProtocol(protocol);
        dnsBuilder.setApp(app);
        dnsBuilder.setMonitorId(monitorId);
        dnsBuilder.setResponseCode(process.exitValue());
        dnsBuilder.setResponseMessage(process.getErrorStream().toString());

        // Add the DNS metrics data to the response
        builder.setDnsMetrics(dnsBuilder.build());
    } catch (IOException | InterruptedException e) {
        // Log the error and set the response code and message accordingly
        logger.error("Error collecting DNS metrics data", e);
        builder.setResponseCode(500);
        builder.setResponseMessage("Error collecting DNS metrics data");
    }
}
