public String decrypt(String encryptedText) {
    try {
        // Step 1: Decode the Base64 encoded encrypted text to get the byte array
        byte[] encryptedBytes = Base64.getDecoder().decode(encryptedText);

        // Step 2: Extract the Initialization Vector (IV) and salt from the byte array
        byte[] iv = Arrays.copyOfRange(encryptedBytes, 0, 16);
        byte[] salt = Arrays.copyOfRange(encryptedBytes, 16, 32);

        // Step 3: Extract the cipher text from the byte array
        byte[] cipherText = Arrays.copyOfRange(encryptedBytes, 32, encryptedBytes.length);

        // Step 4: Generate the AES key from the password and salt
        SecretKeyFactory factory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA256");
        KeySpec spec = new PBEKeySpec(password.toCharArray(), salt, 65536, 256);
        SecretKey tmp = factory.generateSecret(spec);
        SecretKey secret = new SecretKeySpec(tmp.getEncoded(), "AES");

        // Step 5: Initialize the cipher in decryption mode with the AES key and IV
        Cipher cipher = Cipher.getInstance("AES/GCM/NoPadding");
        cipher.init(Cipher.DECRYPT_MODE, secret, new GCMParameterSpec(128, iv));

        // Step 6: Decrypt the cipher text to get the plain text
        byte[] plainText = cipher.doFinal(cipherText);

        // Step 7: Convert the plain text byte array to a string using UTF-8 encoding
        return new String(plainText, StandardCharsets.UTF_8);
    } catch (Exception e) {
        throw new NitriteSecurityException("Error during decryption", e);
    }
}
